mapper.select:
select 'false' as QUERYID,

from user
where Id = #{id,jdbcType=INTEGER}

mapper.update:
update user
set Name = #{name,jdbcType=VARCHAR},
  Password = #{password,jdbcType=VARCHAR},
  Sex = #{sex,jdbcType=VARCHAR},
  Birthday = #{birthday,jdbcType=TIMESTAMP}
where Id = #{id,jdbcType=INTEGER}
